import Icon from "../icon";

import { useState, useEffect } from "react"; 

export default function LoginForm() {
    const [credentials, setCredentials] = useState({username: "", password: ""});

    function onSubmit(e) {
        e.preventDefault();
    }

    return (
        <div className="p-6 m-2 border rounded-md min-w-12 min-h-12 shadow-md bg-white">
            <div className="text-2xl font-bold mb-4 text-center">Admin Login</div>
            <form onSubmit={onSubmit} className="flex flex-col gap-4">
                <div id="field">
                    <label className="block mb-1 text-sm text-zinc-600 " htmlFor="username">Username</label>
                    <input onChange={(e) => {setCredentials({...credentials, username: e.target.value})}} className="w-full p-2 border rounded-md " type="text" id="username" name="username" placeholder="Enter your username" required />
                </div>
                <div id="field">
                    <label className="block mb-1 text-sm text-zinc-600 " htmlFor="password">Password</label>
                    <input onChange={(e) => {setCredentials({...credentials, password: e.target.value})}} className="w-full p-2 border rounded-md " type="password" id="password" name="password" placeholder="Enter your password" required />
                </div>
                <button className="w-full p-2 bg-blue-500 text-white rounded-md  hover:bg-blue-600" type="submit">
                    Login <Icon icon="fa-solid fa-arrow-right" className="inline ml-1" /> </button>
            </form>
        </div>
    )
}
