import { useState } from 'react';

export default function CreateNewElement({ tab, onCreate }) {
    const [formData, setFormData] = useState({});

    return (
        <>
            <div className="w-full h-full z-1000 fixed top-0 left-0 bg-black/30 justify-center items-center flex">
                <div className="bg-white p-6 rounded-md shadow-md w-1/3">
                    <div className="text-2xl font-bold mb-4 text-[rgb(60,60,60)] text-center">Create New {tab.slice(0, -1)}</div>
                    <form onSubmit={(e) => {e.preventDefault(); onCreate(formData);}} className="flex flex-col gap-4">
                        { tab === "Payments" && (
                            <>
                                <input type="text" placeholder="Payer Name" className="border p-2 rounded" onChange={(e) => setFormData({...formData, payer: e.target.value})} />
                                <input type="text" placeholder="Amount Due" className="border p-2 rounded" onChange={(e) => setFormData({...formData, amountDue: e.target.value})} />
                                <input type="number" placeholder="Amount Paid" className="border p-2 rounded" onChange={(e) => setFormData({...formData, amountPaid: e.target.value})} />
                                <input type="date" placeholder="Date Paid" className="border p-2 rounded" onChange={(e) => setFormData({...formData, datePaid: e.target.value})} />

                            </>
                        )}
                    </form>
                </div>
            </div>
        </>
    )
}
