import Icon from "../components/icon";

import { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";

export default function DashboardPage() {
    const { authenticated, username, setUsername } = useAuth();
    
    return (
        <div className="h-screen w-screen items-center flex flex-col bg-[#fbfbfb]">
            <div className="header bg-white w-[90%] self-center flex flex-row justify-between mt-3 rounded-lg p-4 drop-shadow-md">
                <div className="flex flex-row items-center">
                    <button className="p-2 text-[22px] text-zinc-700 mr-4"><Icon icon="fa-solid fa-bars"></Icon></button>
                    <h1 className="text-3xl font-bold text-zinc-700">Columbarium Dashboard</h1>
                </div>
                <div id="accountControl" className="flex flex-row items-center">
                    <button className="p-2 text-[22px] text-zinc-700 mr-4"><Icon icon="fa-solid fa-user" className="mr-3"></Icon>{username !== "" ? username : "User Not Found"}</button>
                </div>
            </div>
        </div>
    )
}
