import Icon from "../components/icon";

import { useState, useEffect } from "react";
import { useAuth } from "../contexts/AuthContext";

export default function DashboardPage() {
    const { authenticated, username, setUsername } = useAuth();
    const [selectedTab, setSelectedTab] = useState("Payments");
    
    return (
        <div className="h-screen w-screen items-center flex flex-col bg-[#fbfbfb]">
            <div className="w-[90%] h-[80%]">
                <div className="header bg-white w-full self-center flex flex-row justify-between mt-3 rounded-lg p-4 drop-shadow-md">
                    <div className="flex flex-row items-center">
                        <button className="p-2 text-[22px] rounded-full hover:bg-black/10 text-zinc-700 mr-4"><Icon icon="fa-solid fa-bars"></Icon></button>
                        <h1 className="text-3xl font-bold text-zinc-700">Columbarium Dashboard</h1>
                    </div>
                    <div id="accountControl" className="flex flex-row items-center">
                        <button className="p-2 hover:underline text-md text-zinc-700 mr-4"><Icon icon="fa-solid fa-user" className="mr-3"></Icon>{username !== "" ? username : "User Not Found"}</button>
                    </div>
                </div>

                <div className="w-[100%] h-[85%] flex flex-row gap-3">
                    {/* Sidebar */}
                    <div className="sidebar bg-white w-[18%] h-full self-start flex flex-col mt-3 rounded-lg p-4 drop-shadow-md gap-1">

                        {/* Tabs */}
                        <button onClick={(e) => setSelectedTab(e.target.innerText)} className="p-2 border-b border-black/8 transition-all duration-500 ease-out text-left rounded-sm hover:bg-black/10 text-zinc-700"><Icon icon="fa-solid fa-credit-card" className="mr-3"></Icon>Payments</button>

                        <button onClick={(e) => setSelectedTab(e.target.innerText)} className="p-2 border-b border-black/8 transition-all duration-500 ease-out text-left rounded-sm hover:bg-black/10 text-zinc-700"><Icon icon="fa-solid fa-address-book" className="mr-3"></Icon>Contacts</button>

                        <button onClick={(e) => setSelectedTab(e.target.innerText)} className="p-2 border-b border-black/8 transition-all duration-500 ease-out text-left rounded-sm hover:bg-black/10 text-zinc-700"><Icon icon="fa-solid fa-id-badge" className="mr-3"></Icon>Occupants</button>
                    </div>

                    
                    {/* Main Content */}
                    <div className="content bg-white w-full h-full flex flex-col mt-3 rounded-lg p-4 drop-shadow-md">

                    </div>
                </div>
            </div>
        </div>
    )
}
