import brand_logo from '../assets/brand_logo.png';
import header_bg from '../assets/header_bg.jpg';

export default function HomePage() {
    return (
        <div className="w-screen h-screen overflow-auto flex-col flex">
            <div id="header" className="w-full min-h-16 py-4 bg-[#003366] flex items-center px-6 text-[#fbfbfb] justify-between">
                <div
                    className="absolute z-[1] inset-0 bg-right bg-no-repeat bg-contain opacity-30"
                    style={{ backgroundImage: `linear-gradient(to left, rgba(0,0,0,0) 25%, rgba(100,100,255,0.9) 65%), url(${header_bg})`
                
                }}  
                />

                <div className="flex flex-row gap-6 pl-4 z-[5]">
                    <img src={brand_logo} alt="Brand Logo" className="h-[80px] shadow-2xl" />
                    <div className="flex flex-col justify-center">
                        <h1 className="text-3xl font-semibold">Mary, Cause of Our Joy Parish</h1>
                        <p className="text-sm">The Roman Catholic Diocese of Paranaque</p>
                    </div>
                </div>
            </div>

              <div id="body" className="h-screen w-screen">
                <h1 className="text-3xl font-bold mb-4">Welcome to the Columbarium Management System</h1>
                <p className="text-lg text-gray-700">This is the home page. Please log in to access the dashboard and manage columbarium records.</p>
            </div>
        </div>
    )
}
