import { useState } from 'react';

export default function EditElement({ tab, elementData, fields, onEdit }) {
    const [formData, setFormData] = useState({ ...elementData });

    const handleChange = (name, value) => {
        setFormData((prev) => ({ ...prev, [name]: value }));
    }

    const renderField = (f) => {
        const value = formData[f.name] ?? '';
        const common = {
            name: f.name,
            value,
            onChange: (e) => handleChange(f.name, e.target.value),
            className: 'border p-2 rounded'
        };
        switch (f.type) {
            case 'text':
            case 'number':
            case 'date':
                return <input type={f.type} {...common} />;
            default:
                return null;
        }
    }

    return (
        <form onSubmit={(e) => {
            e.preventDefault();
            onEdit(formData);
        }}>
            {fields.map(renderField)}
            <button type="submit" className="mt-4 p-2 bg-blue-500 text-white rounded">Save</button>
        </form>
    );
}
